mapper.select:
select

,


from pitaya_customer A
	left outer join pitaya_customer_picture B on A.picture_id = B.id


    
		A.picture_id = #{picture_id,jdbcType=BIGINT}
	
	
		and A.name = #{name,jdbcType=VARCHAR}
	
	
		and A.account = #{account,jdbcType=VARCHAR}
	
	
		and A.password = #{password,jdbcType=VARCHAR}
	
	
		and A.phone = #{phone,jdbcType=VARCHAR}
	
	
		and A.sex = #{sex,jdbcType=INTEGER}
	
	
		and A.email = #{email,jdbcType=VARCHAR}
	
	
		and A.address = #{address,jdbcType=VARCHAR}
	
	
		and A.birthday = #{birthday,jdbcType=BIGINT}
