mapper.select:
select
,
from pitaya_food F left outer join pitaya_food_picture P on F.picture_id = P.id where F.id = #{id,jdbcType=BIGINT}

    select 
    
    from pitaya_food 
    
    
      		
				and recommendation = #{recommendation,jdbcType=INTEGER}
			
			
				and status = #{status,jdbcType=INTEGER}
			
			
				and picture_id = #{picture_id,jdbcType=BIGINT}
			
			
				and name = #{name,jdbcType=VARCHAR}
			
			
				and version = #{version,jdbcType=BIGINT}
			
			
				and period = #{period,jdbcType=BIGINT}
			
			
				and price = #{price,jdbcType=DOUBLE}
			
			
				and category = #{category,jdbcType=VARCHAR}
			
			
				and description = #{description,jdbcType=VARCHAR}

    select 
    
    ,
    
    from pitaya_food F 
    	left outer join pitaya_food_picture P on F.picture_id = P.id
    
      		
				F.recommendation = #{recommendation,jdbcType=INTEGER}
			
			
				and F.status = #{status,jdbcType=INTEGER}
			
			
				and F.picture_id = #{picture_id,jdbcType=BIGINT}
			
			
				and F.name = #{name,jdbcType=VARCHAR}
			
			
				and F.version = #{version,jdbcType=BIGINT}
			
			
				and F.period = #{period,jdbcType=BIGINT}
			
			
				and F.price = #{price,jdbcType=DOUBLE}
			
			
				and F.category = #{category,jdbcType=VARCHAR}
			
			
				and F.description = #{description,jdbcType=VARCHAR}

    select 
    
    ,
    
    from pitaya_food F 
    	left outer join pitaya_food_picture P on F.picture_id = P.id
    
    
      		
				F.recommendation = #{recommendation,jdbcType=INTEGER}
			
			
				and F.status = #{status,jdbcType=INTEGER}
			
			
				and F.picture_id = #{picture_id,jdbcType=BIGINT}
			
			
				and F.name = #{name,jdbcType=VARCHAR}
			
			
				and F.version = #{version,jdbcType=BIGINT}
			
			
				and F.period = #{period,jdbcType=BIGINT}
			
			
				and F.price = #{price,jdbcType=DOUBLE}
			
			
				and F.category = #{category,jdbcType=VARCHAR}
			
			
				and F.description = #{description,jdbcType=VARCHAR}
