mapper.select:
select

,

from pitaya_food_picture

	
		name = #{name,jdbcType=VARCHAR}
	
	
		and last_modify_time = #{last_modify_time,jdbcType=TIMESTAMP}
	
	
		and enabled = #{enabled,jdbcType=BIT}
	
	
		and big_image = #{big_image,jdbcType=LONGVARBINARY}
	
	
		and small_image = #{small_image,jdbcType=LONGVARBINARY}

mapper.update:
update pitaya_food_picture
set name = #{name,jdbcType=VARCHAR},
last_modify_time = #{last_modify_time,jdbcType=TIMESTAMP},
enabled = #{enabled,jdbcType=BIT}
where id = #{id,jdbcType=BIGINT}
