mapper.select:
select

from pitaya_food_picture

	
		name = #{name,jdbcType=VARCHAR}
	
	
		and version = #{version,jdbcType=BIGINT}
	
	
		and enabled = #{enabled,jdbcType=BIT}

select

from pitaya_food_picture

	
		name = #{name,jdbcType=VARCHAR}
	
	
		and version = #{version,jdbcType=BIGINT}
	
	
		and enabled = #{enabled,jdbcType=BIT}
	
	
		and large_image_relative_path = #{large_image_relative_path,jdbcType=VARCHAR}
	
	
		and small_image_relative_path = #{small_image_relative_path,jdbcType=VARCHAR}
	
	
		and large_image_absolute_path = #{large_image_absolute_path,jdbcType=VARCHAR}
	
	
		and small_image_absolute_path = #{small_image_absolute_path,jdbcType=VARCHAR}
	
	
		and large_image_size = #{large_image_size,jdbcType=INTEGER}
	
	
		and small_image_size = #{small_image_size,jdbcType=INTEGER}

mapper.update:
update pitaya_food_picture
set name = #{name,jdbcType=VARCHAR},
version = #{version,jdbcType=BIGINT},
enabled = #{enabled,jdbcType=BIT}
where id = #{id,jdbcType=BIGINT}
