select
	
	,
	
	,
	
	,
	
	,
	
	,
	
	,
	
from pitaya_order O 
	left outer join pitaya_user U on O.user_id = U.id
	left outer join pitaya_customer C on O.customer_id = C.id
	left outer join pitaya_order_table_detail TD on TD.order_id = O.id
	left outer join pitaya_table T on TD.table_id = T.id
	left outer join pitaya_order_food_detail FD on FD.order_id = O.id
	left outer join pitaya_food F on FD.food_id = F.id

	
		O.status = #{status,jdbcType=INTEGER}
	
	
		and O.user_id = #{user_id,jdbcType=BIGINT}
	
	
		and O.customer_id = #{customer_id,jdbcType=BIGINT}
	
	
		and O.customer_count = #{customer_count,jdbcType=INTEGER}
	
	
		and O.modifyTime = #{modifyTime,jdbcType=BIGINT}
	
	
		and O.allowance = #{allowance,jdbcType=DOUBLE}
	
	
		and O.total_price = #{total_price,jdbcType=DOUBLE}
	
	
		and O.prePay = #{prePay,jdbcType=DOUBLE}
	
	
		and O.submit_time = #{submit_time,jdbcType=BIGINT}
	
	
		and O.enabled = #{enabled,jdbcType=BIT}
	
	
	
		
			and U.id = #{user.id,jdbcType=BIGINT}
		
		
		
			and U.modifyTime = #{user.modifyTime,jdbcType=BIGINT}
		
		
			and U.image_size = #{user.image_size,jdbcType=INTEGER}
		
		
			and U.image_relative_path = #{user.image_relative_path,jdbcType=VARCHAR}
		
		
			and U.image_absolute_path = #{user.image_absolute_path,jdbcType=VARCHAR}
		
		
		
			and U.account = #{user.account,jdbcType=VARCHAR}
		
		
			and U.name = #{user.name,jdbcType=VARCHAR}
		
		
			and U.password = #{user.password,jdbcType=VARCHAR}
		
		
			and U.phone = #{user.phone,jdbcType=VARCHAR}
		
		
			and U.sex = #{user.sex,jdbcType=INTEGER}
		
		
			and U.email = #{user.email,jdbcType=VARCHAR}
		
		
			and U.address = #{user.address,jdbcType=VARCHAR}
		
		
			and U.birthday = #{user.birthday,jdbcType=BIGINT}
		
		
			and U.description = #{user.description,jdbcType=VARCHAR}
		
		
			and U.department = #{user.department,jdbcType=INTEGER}
		
		
			and U.sub_system = #{user.sub_system,jdbcType=INTEGER}
		
		
			and U.enabled = #{user.enabled,jdbcType=BIT}
		
	
		
	 	
		
			and C.id = #{customer.id,jdbcType=BIGINT}
		
		
		
			and C.modifyTime = #{customer.modifyTime,jdbcType=BIGINT}
		
		
			and C.image_size = #{customer.image_size,jdbcType=INTEGER}
		
		
