mapper.select:
select

from pitaya_order

	
		status = #{status,jdbcType=INTEGER}
	
	
		and modifyTime = #{modifyTime,jdbcType=TIMESTAMP}
	
	
		and allowance = #{allowance,jdbcType=INTEGER}
	
	
		and submit_time = #{submit_time,jdbcType=TIMESTAMP}
	
	
		and enabled = #{enabled,jdbcType=BIT}
